      credentials
    })
  }
  
  return(
        <header>
        <Link to="/" className="logo">MyBlog</Link>
        <nav>
          {username && (
            <>
            <Link to='/create'>Create new page</Link>
            <a onClick={logout}>Logout</a>
            </>
          )}
          {!username && (
            <>
          <Link to="/login">Login</Link>
          <Link to="/register">Register</Link>
            </>
          )}

        </nav>
      </header>
    )
}
